class Shuffle extends StatefulWidget {
  const Shuffle({Key? key}) : super(key: key);

  static const String routeName = '/shuffle';

  @override
  _ShuffleState createState() => _ShuffleState();
}

class _ShuffleState extends State<Shuffle> {
  int _currentindex = 0;

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      bottomNavigationBar: BottomNavigationBar(
          type: BottomNavigationBarType.fixed,
          currentIndex: _currentindex,
          backgroundColor: primaryPink200,
          selectedItemColor: textOnPrimaryBlack,
          unselectedItemColor: secondaryPink800,
          selectedFontSize: 18.0,
          unselectedFontSize: 18.0,
          onTap: (value) {
            // add what you want to do after tapped
            setState(() => _currentindex = value);
          },
          items: [
            BottomNavigationBarItem(icon: Icon(Icons.home), label: 'Home'),
            BottomNavigationBarItem(icon: Icon(Icons.search), label: 'Search'),
            BottomNavigationBarItem(
                icon: Icon(Icons.shuffle), label: 'Shuffle'),
            BottomNavigationBarItem(icon: Icon(Icons.add), label: 'Add')
          ]),
      body: Stack(
        fit: StackFit.expand,
        children: [
          Padding(
            padding: const EdgeInsets.only(top: 100.0),
            child: Column(
              children: [Cards()],
            ),
          ),
        ],
      ),
    );
  }
}

class Cards extends StatefulWidget {
  final Function? onCardChanged;

  Cards({this.onCardChanged});

  @override
  _CardsState createState() => _CardsState();
}

class _CardsState extends State<Cards> with SingleTickerProviderStateMixin {
  var cards = [
    ShuffleCard(
        profile_image:
            'data:image/jpeg;base64,',
        index: 00001,
        name: 'Harry',
        surname: 'Potter',
        MBTI_type: 'ESFP'),
    ShuffleCard(
        profile_image:
            'data:image/jpeg;base64,',
        index: 00002,
        name: 'John',
        surname: 'Snow',
        MBTI_type: 'INTP'),
    ShuffleCard(
        profile_image:
            'data:image/jpeg;base64,',
        index: 00003,
        name: 'Walter',
        surname: 'White',
        MBTI_type: 'ISFJ'),
  ];

  late int currentindex;
  late AnimationController controller;
  late CurvedAnimation curvedAnimation;
  late Animation<Offset> animation;

  @override
  void initState() {
    super.initState();
    currentindex = 0;
    controller = AnimationController(
      vsync: this,
      duration: Duration(milliseconds: 150),
    );
    curvedAnimation =
        CurvedAnimation(parent: controller, curve: Curves.easeOut);
    animation = Tween(begin: Offset(0.0, 0.05), end: Offset(0.0, 0.0))
        .animate(curvedAnimation);
  }

  void cardDrag(DragEndDetails details) {
    controller.reverse().whenComplete(() {
      setState(() {
        controller.reset();
        ShuffleCard removedCard = cards.removeAt(0);
        cards.add(removedCard);
        currentindex = cards[0].index;
      });
    });
  }

  Offset stackedCard(ShuffleCard card) {
    int diff = card.index - currentindex;
    if (card.index == currentindex + 1) {
      return animation.value;
    } else if (diff > 0 && diff <= 2) {
      return Offset(0.0, 0.05 * diff);
    } else {
      return Offset(0.0, 0.0);
    }
  }

  @override
  Widget build(BuildContext context) {
    return Stack(
        overflow: Overflow.visible,
        children: cards.reversed.map((card) {
          if (cards.indexOf(card) <= 2) {
            return GestureDetector(
                onHorizontalDragEnd: cardDrag,
                child: FractionalTranslation(
                  translation: stackedCard(card),
                  child: card,
                ));
          } else {
            return Container();
          }
        }).toList());
  }
}
